import 'package:flutter/material.dart';

void main() {
  runApp(const MyApp());
}

class MyApp extends StatelessWidget {
  const MyApp({super.key});

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Daftar Berita',
      theme: ThemeData(primarySwatch: Colors.blue),
      home: const NewsListPage(),
    );
  }
}

class NewsListPage extends StatelessWidget {
  const NewsListPage({super.key});

  final List<Map<String, String>> news = const [
    {
      "title":
          "Apple Resmi Perkenalkan iPhone 17",
      "subtitle":
          "Model standar iPhone 17 sekarang mendukung refresh rate adaptif 120Hz, brightness puncak dan durabilitas lebih baik.",
      "image":
          "data:image/jpeg;base64,",
    },
    {
      "title":
          "Sensor 48MP Kini Ada di Semua Kamera Belakang iPhone 17 Pro series",
      "subtitle":
          "Semua kamera belakang Pro kini memakai sensor 48MP, mendukung ProRAW & ‘fusion zoom’.",
      "image":
          "data:image/jpeg;base64,",
    },
    {
      "title":
          "Apple resmi rilis iPhone Air",
      "subtitle":
          "iPhone Air, iPhone paling tipis yang pernah ada. Dengan kekuatan pro di dalam nya.",
      "image":
          "https://tse4.mm.bing.net/th?id=OIF.ysm9WqHEnodDcUa%2f9wGVsw&pid=Api&P=0&h=180",
    },
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar:  AppBar(
  backgroundColor: Colors.white, // background AppBar jadi putih
  elevation: 0, // biar rata, tanpa bayangan (opsional)
  title: const Text(
    "Daftar Berita",
    style: TextStyle(
      color: Colors.black, // teks jadi hitam
      fontWeight: FontWeight.bold,
    ),
  ),
),
      body: ListView.builder(
        itemCount: news.length,
        itemBuilder: (context, index) {
          return ListTile(
            leading: ClipRRect(
              borderRadius: BorderRadius.circular(8),
              child: Image.network(
                news[index]["image"]!,
                width: 80,
                height: 80,
                fit: BoxFit.cover,
              ),
            ),
            title: Text(
              news[index]["title"]!,
              style: const TextStyle(
                fontSize: 16,
                fontWeight: FontWeight.bold,
                color: Colors.black, // teks lebih hitam
              ),
            ),
            subtitle: Text(
              news[index]["subtitle"]!,
              style: const TextStyle(
                fontSize: 14,
                color: Colors.black87, // lebih gelap dari default abu
              ),
            ),
            trailing: const Icon(Icons.bookmark_border),
            onTap: () {
              ScaffoldMessenger.of(context).showSnackBar(
                const SnackBar(
                  content: Text("Mengalihkan ke halaman berita"),
                  duration: Duration(seconds: 2),
                ),
              );
            },
          );
        },
      ),
    );
  }
}